mapper.select:
select count(*) from
general_bo join board on (board_no = general_no) where general_type =
#{generalType}

mapper.update:
update member
set status = #{status}, member_drop = #{memberDrop}
where member_id =
#{memberId}

update member
set member_point = member_point + #{memberPoint}
where member_no = #{memberNo}
