mapper.select:
select count(*)
from board
where board_status = 'N' and board_type = #{boardType}

select count(*)
from board
	left join member on(board_writer_no = member_no)
	left join general_bo on(general_no = board_no)
	left join local using(local_no)
where board_status = 'N' and board_type = #{boardType}

	and general_type = #{generalType}


	and local_name = #{localName}


	and board_title like '%${searchWord}%'

select *
from board
	left join member on(board_writer_no = member_no)
	left join general_bo on(general_no = board_no)
	left join local using(local_no)
where board_status = 'N' and board_type = #{boardType}

	and general_type = #{generalType}


	and local_name = #{localName}


	and board_title like '%${searchWord}%'

order by board_no desc

mapper.update:
update board
set board_count = board_count + 1
where board_no = #{boardNo}

update board
set board_status = 'Y'
where board_no = #{bNo}

update reply
set reply_status = 'Y'
where reply_no = #{rNo}

update reply
set reply_content = #{replyContent}, reply_modify_date = sysdate
where reply_no = #{replyNo}

mapper.select:
select count(*) from board left join member on(board_writer_no = member_no) left join general_ask on(general_no = board_no) where board_status = 'N' and board_type = #{boardType}
and general_type = #{generalType}
and board_title like '%${searchWord}%'

select * from board left join member on(board_writer_no = member_no) left join general_ask on(general_no = board_no) where board_status = 'N' and board_type = #{boardType}
and general_type = #{generalType}
and board_title like '%${searchWord}%'
order by board_no desc

mapper.insert:
update board
set board_title = #{boardTitle}, board_content = #{boardContent}, board_modify_date = sysdate
where board_no = #{boardNo}

mapper.update:
update question
set question_pwd = #{boardTitle}
where question_no = #{boardNo}

mapper.select:
select general_type
from general_ask
where general_no = #{boardNo}

select count(*)
from board
where board_writer_no = #{memberNo}

mapper.update:
update member
set member_point = member_point + 1000
where member_no = #{memberNo}
