mapper.select:
select count(*)
from planner_like
where planner_no = #{ pNo }

select count(*)
from planner_like
where planner_no = #{ pNo } and member_no = #{ loginUserNo }

select round(avg(review_star),2)
from review
where rev_ref_type = 'PLANNER' and review_status='Y' and rev_ref_no = #{ pNo }

select count (*)
from review
where rev_ref_type = 'PLANNER' and review_status='Y' and rev_ref_no = #{ pNo }

mapper.update:
update review
set review_status = 'N'
where review_no = #{rNo}
