mapper.select:
select count(*)
from member
where member_id = #{id}

mapper.update:
update member
set PHONE=#{phoneNo}, NICKNAME=#{nickName}, EMAIL=#{email}
where MEMBER_ID=#{id}

update member
set MEMBER_PWD = #{newPwd}
where MEMBER_ID = #{id}

update MEMBER 
set STATUS = 'N' 
where MEMBER_NO = #{memberNo}

update BOARD
set BOARD_STATUS = 'N'
where BOARD_WRITER_NO = #{memberNo}

update REPLY
set REPLY_STATUS = 'N'
where REPLY_WRITER_NO = #{memberNo}

update SCHEDULE
set SC_DEL_STATUS = 'N'
where SC_WRITER_NO = #{memberNo}

update REVIEW
set REVIEW_STATUS = 'N'
where MEMBER_NO = #{memberNo}

mapper.select:
select count(*)
from IMAGE
where image_ref_type = 'MEMBER' and image_ref_no = #{memberNo}

mapper.delete:
delete
from IMAGE
where IMAGE_REF_TYPE = 'MEMBER' and IMAGE_REF_NO = #{memberNo}

mapper.select:
select IMAGE_RENAME
from IMAGE
where IMAGE_REF_TYPE = 'MEMBER' and IMAGE_REF_NO = #{memberNo}
