mapper.select:
select count(*)
from reco_place
where place_no = #{placeNo} and local_no = #{localNo}

mapper.update:
update reco_place
	set place_count = place_count + 1
	where place_no = #{placeNo} and local_no = #{localNo} and content_type_id = #{contentTypeId}

mapper.select:
select *
from Image
where image_ref_type = 'REVIEW' and image_ref_no = #{rId}

mapper.update:
update review
set review_title = #{reviewTitle}, review_content = #{reviewContent}, review_star = #{reviewStar}
where review_no = #{reviewNo}

mapper.select:
select count(*)
from reco_place
where place_no = #{placeNo} and place_name = #{placeName}

mapper.update:
update reco_place
set place_name = #{placeName}
where place_no = #{placeNo}
