mapper.select:
select count(*)
from reco_place
where place_no = #{contentid} and local_no = #{areaCode}

select *
from place
where place_no = #{contentid} and local_no = #{areaCode}

mapper.update:
update place
	set place_count = place_count + 1
	where place_no = #{contentid} and local_no = #{areaCode}

mapper.select:
select *
from review
where REV_REF_TYPE = 'PLACE' and REV_REF_NO = #{contentid}
